mapper.select:


	 and id = #{id} 
	 and name = #{name} 
	 and avatar = #{avatar} 
	 and password = #{password} 
	 and pw = #{pw} 
	 and phone = #{phone} 
	 and role_id = #{roleId} 
	 and status = #{status} 
	 and create_by_aid = #{createByAid} 
	 and create_time = #{createTime} 
	 and login_time = #{loginTime} 
	 and login_ip = #{loginIp} 
	 and login_count = #{loginCount} 
	 and user_type = #{userType} 

order by

	 id desc 
	 name desc 
	 avatar desc 
	 password desc 
	 pw desc 
	 phone desc 
	 role_id desc 
	 status desc 
	 create_by_aid desc 
	 create_time desc 
	 login_time desc 
	 login_ip desc 
	 login_count desc 
	 user_type desc 
	 id desc
