mapper.select:


	 and id = #{id} 
	 and semic_dictionary_id = #{semicDictionaryId} 
	 and abbreviation = #{abbreviation} 
	 and description = #{description} 
	 and create_time = #{createTime} 
	 and create_by = #{createBy} 
	 and update_time = #{updateTime} 
	 and update_by = #{updateBy} 
	 and full_name = #{fullName} 

order by

	 id desc 
	 semic_dictionary_id desc 
	 abbreviation desc 
	 description desc 
	 create_time desc 
	 create_by desc 
	 update_time desc 
	 update_by desc 
	 full_name desc 
	 id desc
