mapper.select:


	 and id = #{id} 
	 and semic_type_id = #{semicTypeId} 
	 and full_name = #{fullName} 
	 and abbreviation = #{abbreviation} 
	 and description = #{description} 
	 and create_time = #{createTime} 
	 and create_by = #{createBy} 
	 and update_time = #{updateTime} 
	 and update_by = #{updateBy} 

order by

	 id desc 
	 semic_type_id desc 
	 full_name desc 
	 abbreviation desc 
	 description desc 
	 create_time desc 
	 create_by desc 
	 update_time desc 
	 update_by desc 
	 id desc
