mapper.select:


	 and id = #{id} 
	 and user_id = #{userId} 
	 and category_id = #{categoryId} 
	 and title = #{title} 
	 and account = #{account} 
	 and password = #{password} 
	 and url = #{url} 
	 and notes = #{notes} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 

order by

	 id desc 
	 user_id desc 
	 category_id desc 
	 title desc 
	 account desc 
	 password desc 
	 url desc 
	 notes desc 
	 create_by desc 
	 create_time desc 
	 update_by desc 
	 update_time desc 
	 id desc
